def solution(s, n):
    upper_ch = "ABCDEFGHIJKLMNOPQRSTUVWXYZ"
    answer = ""
    for el_s in s:
        #1.ë§¨ ë§ˆì§€ë§‰ ë¦¬í„´ì„ ìœ„í•´ ì›ë˜ ë¬¸ìê°€ ì†Œë¬¸ìì¸ì§€ ëŒ€ë¬¸ìì¸ì§€ ê¸°ì–µ and ëŒ€ë¬¸ìì—ì„œ ê²€ìƒ‰í• ê²ƒì´ê¸° ë•Œë¬¸ì— ëŒ€ë¬¸ìë¡œ ë³€í™˜
        #ëŒ€ë¬¸ìë¡œ ë³€í™˜ëœ ë¬¸ìì—´ sì˜ ê°ê°ì˜ ë¬¸ì,ëŒ€ì†Œë¬¸ì ì—¬ë¶€
        if el_s == " ":
            answer += " "
        elif el_s.isupper() == True:
            was_upper = True
        else:
            was_upper = False
            el_s = el_s.upper()
        #2.ì¸ë±ìŠ¤ ìˆ«ìê°€ upper_chì—ì„œ ë²—ì–´ë‚ ë•Œ ì•„ë‹ë•Œ ì²˜ë¦¬
        for idx,up_ch in enumerate(upper_ch):
            if el_s == up_ch and idx + n <= len(upper_ch)-1:
                find_idx = idx+n
                if was_upper == True:
                    answer += upper_ch[find_idx]
                else:
                    answer += upper_ch[find_idx].lower()        
            elif el_s == up_ch and idx + n > len(upper_ch)-1:
                find_idx = n-len(upper_ch[idx:])
                if was_upper == True:
                    answer += upper_ch[find_idx]
                else:
                    answer += upper_ch[find_idx].lower()  
    return answer



