def caesar(s, n):
    lower_list = "abcdefghijklmnopqrstuvwxyz"  
    #ì†Œë¬¸ìë„ ë¦¬ìŠ¤íŠ¸ë¡œ ë§Œë“¦ 
    #ì¢‹ì€ì ->ëŒ€ì†Œë¬¸ì ì—¬ë¶€ë¥¼ ê¸°ì–µí•˜ëŠ” ì½”ë“œ ë¶ˆí•„ìš”(ì¡°ê±´ë¬¸)
    #ì•ˆì¢‹ì€ì ->ì†Œë¬¸ìë¡œ ì´ë¤„ì§„ ë¦¬ìŠ¤íŠ¸ë¥¼ ë§Œë“œëŠ”ë° ê·¸ë§Œí¼ì˜ ë©”ëª¨ë¦¬ í•„ìš”
    upper_list = "ABCDEFGHIJKLMNOPQRSTUVWXYZ" 



    result = [] 
    #ë¬¸ìì—´ë“¤ì„ ì €ì¥í•  listë¥¼ ë§Œë“¦
    #mutableë¡œ ë¶™ì´ëŠ” ê²ƒê³¼ immutableë¡œ ë¶™ì´ëŠ” ê²ƒ ì°¨ì´
    #ì†ë„ -> 
    #mutableì´ ë” ë¹ ë¦„,
    #ê·¸ëŸ¬ë‚˜ garbage collectorë„ ê³ ë ¤ì‹œ ìŠ¤ìº”ë²”ìœ„ê°€ ë„ˆë¬´ì»¤ì„œ ëŠë ¤ì§ˆìˆ˜ë„?
    #ë©”ëª¨ë¦¬ -> immutableì´ ë” ì ê²Œë“¤ì„ ë“¯(ë¦¬ìŠ¤íŠ¸ëŠ” ì´ì¤‘í¬ì¸í„°ê°™ì€ êµ¬ì¡°ë¼ì„œ ì´ë ‡ê²Œ ì˜ˆìƒë¨)
    for i in s:
        if i == " ":
            result.append(" ")
        elif i.islower() is True:
            new_ = lower_list.find(i) + n
            result.append(lower_list[new_ % 26]) 
            #ë‚˜ë¨¸ì§€ë¡œ ê³„ì‚°í•˜ëŠ” ë°©ì‹,ì´ê²Œ ë” ê°„ë‹¨í•˜ê³  ì¢‹ì€ë“¯
            #ë°˜ë³µë¬¸ì´ ë¬¸ìì—´ì— ëŒ€í•´ì„œ ëŒë‹¤ë³´ë‹ˆ ë¬¸ìì—´ê³¼ ë¬¸ìì—´ì˜ ì¸ë±ìŠ¤ ìœ„ì£¼ë¡œ ë„ˆë¬´ ìƒê°í•¨
            #ì–´ë–¤ ìˆ«ì(ì—¬ê¸°ì„œëŠ” ì¸ë±ìŠ¤)ë³´ë‹¤ í¬ê±°ë‚˜ ê°™ì„ë•Œì— ë‹¤ì‹œ 0ë¶€í„° ì¤˜ì•¼í•˜ëŠ” ìƒí™©? -> ë‚˜ë¨¸ì§€ í™œìš©
        else:
            new_ = upper_list.find(i) + n
            result.append(upper_list[new_ % 26])
    return "".join(result)

